GimmicField::GimmicField()
{
	groundHandle = my::myLoadGraph("data/FieldGraph/Tiles-1.png");
	GetGraphSize(groundHandle, &gGroundGraphWidth, &gGroundGraphHeight);
}

GimmicField::~GimmicField()
{
	DeleteGraph(groundHandle);
}

void GimmicField::update()
{
	for (int x = 0; x < gTutorialNumX; x++) {
		for (int y = 0; y < gBgNumY + 1; y++) {

			const int chipNo = gimmicFieldData::gimmicGround[y][x];

			if (chipNo == 60) {
				gimmicFieldData::gimmicGround[y][x] = 0;
			}

		}
	}
}

void GimmicField::draw(Vec2 offset)
{
	for (int x = 0; x < gTutorialNumX; x++) {

		int posX = x * gChipSize + static_cast<int>(offset.x) + gGraphChipSize;

		if (posX > Game::kScreenWidth + gChipSize) {
			break;
		}
		if (posX < -gChipSize) {
			continue;
		}

		for (int y = 0; y < gBgNumY + 1; y++) {

			const int chipNo = gimmicFieldData::gimmicGround[y][x];

			assert(chipNo >= 0);
			assert(chipNo < chipNum(gGroundGraphWidth, gGroundGraphHeight));


			int posY = y * gChipSize + gGraphChipSize;

			int graphX = (chipNo % chipNumX(gGroundGraphWidth)) * gGraphChipSize;
			int graphY = (chipNo / chipNumX(gGroundGraphWidth)) * gGraphChipSize;


			DrawRectRotaGraph(posX, posY, graphX, graphY, gGraphChipSize, gGraphChipSize, 2.0f, 0.0f, groundHandle, true, false);

		}
	}
}

bool GimmicField::examinationChip(int y, int x)
{
	if (gimmicFieldData::gimmicGround[y][x] != 0) {
		return true;
	}
	return false;
}

int GimmicField::chipNumX(int width)
{
	return (width / gGraphChipSize);
}

int GimmicField::chipNumY(int height)
{
	return (height / gGraphChipSize);
}

int GimmicField::chipNum(int width, int height)
{
	return (chipNumX(width) * chipNumY(height));
}